import React from 'react';
const ProductDetail = ({ product, onClose }) => {
  return (
    <div style={detailStyles.overlay}>
      <div style={detailStyles.modal}>
        <button onClick={onClose} style={detailStyles.closeButton}>Đóng</button>
        <h2 style={detailStyles.title}>{product.name}</h2>
        <img src={product.image} alt={product.name} style={detailStyles.image} />
        <p style={detailStyles.description}>{product.description}</p>
        <p style={detailStyles.price}>Giá: {product.price}</p>
      </div>
    </div>
  );
};

const detailStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 9999,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modal: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '20px',
    maxWidth: '80%',
    maxHeight: '80%',
    overflow: 'auto',
    textAlign: 'center',
    position: 'relative',
  },
  closeButton: {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: '#cdcdcd',
    color: '#000',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '5px',
    fontSize: '0.8rem',
    cursor: 'pointer',
  },
  title: {
    fontSize: '1.8rem',
    color: '#333',
    marginBottom: '10px',
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
    marginBottom: '10px',
  },
  description: {
    fontSize: '1rem',
    color: '#666',
    marginBottom: '10px',
  },
  price: {
    fontSize: '1.2rem',
    color: '#000',
    fontWeight: 'bold',
  },
};

export default ProductList;
