import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Cart.css'; // Import CSS file for styling

const Cart = () => {
  const location = useLocation();
  const [quantity, setQuantity] = useState(1);
  const product = location.state?.product; // Optional chaining

  const handleIncreaseQuantity = () => {
    setQuantity(Math.min(quantity + 1, 10)); // Limit quantity to 10
  };

  const handleDecreaseQuantity = () => {
    setQuantity(Math.max(quantity - 1, 1)); // Prevent negative quantity
  };

  const handleRemoveFromCart = () => {
    // Implement logic to remove product from cart (replace with your cart actions)
    console.log('Product removed from cart'); // Placeholder for now
  };

  if (!product) {
    return (
      <div className="cart-container">
        <h2>Giỏ hàng của bạn</h2>
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      </div>
    );
  }

  // Calculate total price with proper formatting (assuming currency)
  const totalPrice = (product.price * quantity).toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <div className="cart-container">
      <h2>Giỏ hàng của bạn</h2>
      <div className="cart-item">
        <p>Tên sản phẩm: {product.name}</p>
        <p>Giá: {product.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</p>
        <div className="quantity-control">
          <label htmlFor="quantity">Số lượng: </label>
          <div className="quantity-buttons">
            <button onClick={handleDecreaseQuantity}>-</button>
            <input
              type="number"
              id="quantity"
              name="quantity"
              value={quantity}
              onChange={(e) => setQuantity(Math.max(parseInt(e.target.value, 10), 1))}
              min={1}
              max={10}
              className="quantity-input"
            />
            <button onClick={handleIncreaseQuantity}>+</button>
          </div>
        </div>
        <button onClick={handleRemoveFromCart} className="remove-button">Xóa khỏi giỏ hàng</button>
      </div>
      <div className="cart-summary">
        <h3>Tổng tiền: {totalPrice}</h3>
        <button className="checkout-button">Thanh toán</button>
      </div>
    </div>
  );
};

export default Cart;
