import React from 'react';

const Checkout = () => {
  return (
    <div style={styles.container}>
      <div style={styles.orderSummary}>
        <h2 style={styles.sectionTitle}>Đơn hàng của bạn</h2>
        <div style={styles.orderItem}>
          <div style={styles.itemName}>Serum tế bào gốc cây đa</div>
          <div style={styles.itemPrice}>250.000đ</div>
        </div>
        <div style={styles.orderItem}>
          <div style={styles.itemName}>Sushi set Premium</div>
          <div style={styles.itemPrice}>750.000đ</div>
        </div>
        <div style={styles.totalPrice}>Tổng tiền: <span style={styles.totalAmount}>1.000.000đ</span></div>
      </div>
      <div style={styles.customerInfo}>
        <h2 style={styles.sectionTitle}>Thông tin khách hàng</h2>
        <form style={styles.form}>
          <input type="text" placeholder="Họ và tên" style={styles.input} />
          <input type="text" placeholder="Số điện thoại" style={styles.input} />
          <input type="text" placeholder="Địa chỉ" style={styles.input} />
          <input type="text" placeholder="Email" style={styles.input} />
          <button type="submit" style={styles.submitButton}>Hoàn thành đặt hàng</button>
        </form>
      </div>
    </div>
  );
};

export default Checkout;

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
  },
  orderSummary: {
    flex: '1',
    marginRight: '20px',
  },
  sectionTitle: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  orderItem: {
    marginBottom: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  itemName: {
    flex: '1',
    marginRight: '10px',
  },
  itemPrice: {
    fontWeight: 'bold',
  },
  totalPrice: {
    marginTop: '20px',
    fontSize: '20px',
    fontWeight: 'bold',
  },
  totalAmount: {
    color: '#ff5f00',
  },
  customerInfo: {
    flex: '1',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    marginBottom: '10px',
    padding: '8px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  submitButton: {
    padding: '10px',
    borderRadius: '4px',
    backgroundColor: '#ff5f00',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
  },
};
