import React, { useState } from 'react';

const AdminPage = () => {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({ name: '', price: 0 });
  const [editProductIndex, setEditProductIndex] = useState(null);
  const [editedProduct, setEditedProduct] = useState({ name: '', price: 0 });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editProductIndex !== null) {
      setEditedProduct({ ...editedProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleAddProduct = () => {
    setProducts([...products, newProduct]);
    setNewProduct({ name: '', price: 0 });
  };

  const handleDeleteProduct = (index) => {
    const updatedProducts = [...products];
    updatedProducts.splice(index, 1);
    setProducts(updatedProducts);
  };

  const handleEditProduct = (index) => {
    setEditProductIndex(index);
    setEditedProduct(products[index]);
  };

  const handleSaveEdit = () => {
    const updatedProducts = [...products];
    updatedProducts[editProductIndex] = editedProduct;
    setProducts(updatedProducts);
    setEditProductIndex(null);
    setEditedProduct({ name: '', price: 0 });
  };

  return (
    <div>
      <h2>Quản lý cửa hàng</h2>

      {/* Form để thêm sản phẩm mới */}
      <div>
        <h3>Thêm sản phẩm mới</h3>
        <label>
          Tên sản phẩm:
          <input type="text" name="name" value={newProduct.name} onChange={handleInputChange} />
        </label>
        <label>
          Giá:
          <input type="number" name="price" value={newProduct.price} onChange={handleInputChange} />
        </label>
        <button onClick={handleAddProduct}>Thêm sản phẩm</button>
      </div>

      {/* Danh sách sản phẩm */}
      <div>
        <h3>Danh sách sản phẩm</h3>
        <ul>
          {products.map((product, index) => (
            <li key={index}>
              <div>Tên: {index === editProductIndex ? <input type="text" name="name" value={editedProduct.name} onChange={handleInputChange} /> : product.name}</div>
              <div>Giá: {index === editProductIndex ? <input type="number" name="price" value={editedProduct.price} onChange={handleInputChange} /> : product.price}</div>
              {index === editProductIndex ? (
                <button onClick={handleSaveEdit}>Lưu</button>
              ) : (
                <>
                  <button onClick={() => handleEditProduct(index)}>Chỉnh sửa</button>
                  <button onClick={() => handleDeleteProduct(index)}>Xóa</button>
                </>
              )}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AdminPage;
