import React, { useState } from 'react';

const Checkout = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    phoneNumber: '',
    address: '',
    email: '',
    paymentMethod: '',
    agreedToTerms: false
  });
  const [formErrors, setFormErrors] = useState({});
  const [isFormValid, setIsFormValid] = useState(false);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const inputValue = type === 'checkbox' ? checked : value;
    setFormData({ ...formData, [name]: inputValue });
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Process the order here
      console.log('Order submitted:', formData);
    }
  };

  const validateForm = () => {
    const errors = {};
    let formIsValid = true;

    if (!formData.fullName.trim()) {
      errors.fullName = 'Hãy nhập họ và tên của bạn';
      formIsValid = false;
    }

    if (!formData.phoneNumber.trim()) {
      errors.phoneNumber = 'Hãy nhập số điện thoại của bạn';
      formIsValid = false;
    }

    if (!formData.address.trim()) {
      errors.address = 'Hãy nhập địa chỉ của bạn';
      formIsValid = false;
    }

    if (!formData.email.trim()) {
      errors.email = 'Hãy nhập địa chỉ email của bạn';
      formIsValid = false;
    }

    if (!formData.paymentMethod) {
      errors.paymentMethod = 'Hãy chọn phương thức thanh toán';
      formIsValid = false;
    }

    if (!formData.agreedToTerms) {
      errors.agreedToTerms = 'Bạn phải đồng ý với các điều khoản và điều kiện';
      formIsValid = false;
    }

    setFormErrors(errors);
    setIsFormValid(formIsValid);
    return formIsValid;
  };

  return (
    <div style={styles.container}>
      <div style={styles.orderSummary}>
        <h2 style={styles.sectionTitle}>Đơn hàng của bạn</h2>
        <div style={styles.orderItem}>
          <div style={styles.itemName}>Serum tế bào gốc cây đa</div>
          <div style={styles.itemPrice}>250.000đ</div>
          <div style={styles.quantity}>Số lượng: 1</div>
        </div>
        <div style={styles.orderItem}>
          <div style={styles.itemName}>Mặt nạ dưỡng trắng như Bạch Cốt Tinh</div>
          <div style={styles.itemPrice}>750.000đ</div>
          <div style={styles.quantity}>Số lượng: 1</div>
        </div>
        <div style={styles.totalPrice}>Tổng tiền: <span style={styles.totalAmount}>1.000.000đ</span></div>
      </div>
      <div style={styles.customerInfo}>
        <h2 style={styles.sectionTitle}>Thông tin khách hàng</h2>
        <form style={styles.form} onSubmit={handleFormSubmit}>
          <input type="text" name="fullName" placeholder="Họ và tên" style={styles.input} value={formData.fullName} onChange={handleInputChange} />
          {formErrors.fullName && <span style={styles.error}>{formErrors.fullName}</span>}
          <input type="text" name="phoneNumber" placeholder="Số điện thoại" style={styles.input} value={formData.phoneNumber} onChange={handleInputChange} />
          {formErrors.phoneNumber && <span style={styles.error}>{formErrors.phoneNumber}</span>}
          <input type="text" name="address" placeholder="Địa chỉ" style={styles.input} value={formData.address} onChange={handleInputChange} />
          {formErrors.address && <span style={styles.error}>{formErrors.address}</span>}
          <input type="text" name="email" placeholder="Email" style={styles.input} value={formData.email} onChange={handleInputChange} />
          {formErrors.email && <span style={styles.error}>{formErrors.email}</span>}
          <select name="paymentMethod" style={styles.input} value={formData.paymentMethod} onChange={handleInputChange}>
            <option value="">Chọn phương thức thanh toán</option>
            <option value="cash">Tiền mặt</option>
            <option value="credit">Thẻ tín dụng</option>
            <option value="bank">Chuyển khoản ngân hàng</option>
          </select>
          {formErrors.paymentMethod && <span style={styles.error}>{formErrors.paymentMethod}</span>}
          <label>
            <input type="checkbox" name="agreedToTerms" checked={formData.agreedToTerms} onChange={handleInputChange} />
            Tôi đồng ý với các điều khoản và điều kiện
          </label>
          {formErrors.agreedToTerms && <span style={styles.error}>{formErrors.agreedToTerms}</span>}
          <button type="submit" style={styles.submitButton} disabled={!isFormValid}>Hoàn thành đặt hàng</button>
        </form>
      </div>
    </div>
  );
};

export default Checkout;

const styles = {
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '50px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
  },
  orderSummary: {
    flex: '1',
    marginRight: '20px',
  },
  sectionTitle: {
    fontSize: '24px',
    marginBottom: '20px',
  },
  orderItem: {
    marginBottom: '10px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  itemName: {
    flex: '1',
    marginRight: '10px',
  },
  itemPrice: {
    fontWeight: 'bold',
  },
  quantity: {
    margin:'10px',
    fontWeight: 'bold',
  },
  totalPrice: {
    marginTop: '20px',
    fontSize: '20px',
    fontWeight: 'bold',
  },
  totalAmount: {
    color: '#ff5f00',
  },
  customerInfo: {
    flex: '1',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
  },
  input: {
    marginBottom: '10px',
    padding: '15px',
    borderRadius: '4px',
    border: '1px solid #ccc',
  },
  submitButton: {
    padding: '20px',
    borderRadius: '4px',
    backgroundColor: '#ff5f00',
    color: '#fff',
    border: 'none',
    cursor: 'pointer',
    fontSize: '16px',
  },
  error: {
    color: 'red',
    fontSize: '14px',
  },
};
