import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Header() {
    const [showSearchDesktop, setShowSearchDesktop] = useState(false);
    const [searchInput, setSearchInput] = useState('');

    useEffect(() => {
        // Your useEffect code here
    }, []);

    const handleChange = (e) => {
        setSearchInput(e.target.value);
    };

    return (
        <header className="main-header">
            <div className="cm-width">
                <div className="topbar_header">
                    <div className="topbar-inner">
                        <div className="topbar-left">
                            {/* Your left section content */}
                        </div>
                        <div className="topbar-right">
                            {/* Your right section content */}
                        </div>
                    </div>
                </div>
                <div className="header-main">
                    <div className="main-header--top">
                        <div className="logo-menu">
                            {/* Your logo section content */}
                        </div>
                        <div className="header-form-search">
                            <input
                                placeholder="Bạn đang tìm kiếm gì"
                                type="search"
                                value={searchInput}
                                onClick={() => setShowSearchDesktop(true)}
                                onKeyDown={() => setShowSearchDesktop(true)}
                                onChange={handleChange}
                            />
                            {showSearchDesktop && (
                                <div className="search-complete">
                                    {/* Your search suggestion content */}
                                </div>
                            )}
                        </div>
                        <div className="main-header--top__right header-mode">
                            {/* Your right section content */}
                        </div>
                    </div>
                </div>
            </div>
        </header>
    );
}

export default Header;
