import React from 'react';
import logoImage from './logo192.png'; // Import the logo image

const Header = () => {
  return (
    <header style={styles.header}>
      <div style={styles.container}>
        <img src={logoImage} alt="Hasaki Logo" style={styles.logoImage} /> {/* Logo image */}
        <h1 style={styles.logo}>Hasaki</h1>
        <nav style={styles.nav}>
          <ul style={styles.navList}>
            <li style={styles.navItem}><a href="/" style={styles.navLink}>Trang Chủ</a></li>
            <li style={styles.navItem}><a href="/products" style={styles.navLink}>Sản Phẩm</a></li>
            <li style={styles.navItem}><a href="/cart" style={styles.navLink}>Giỏ Hàng</a></li>
            {/* Thêm các liên kết và mục điều hướng khác nếu cần */}
          </ul>
        </nav>
        <div style={styles.rightSection}>
          <input type="text" placeholder="Tìm kiếm..." style={styles.searchInput} />
          <a href="/cart" style={styles.cartLink}>
            <i className="fa fa-shopping-cart"></i> Giỏ hàng
          </a>
        </div>
      </div>
    </header>
  );
}

const styles = {
  header: {
    backgroundColor: '#326e51',
    color: '#fff',
    padding: '20px 0',
    borderBottom: '1px solid #ddd',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  logoImage: {
    width: '50px', // Adjust width as needed
    height: '50px', // Adjust height as needed
    marginRight: '10px', // Add some margin to separate from the text
  },
  logo: {
    margin: 0,
    fontSize: '2.5rem',
    fontFamily: 'Arial, sans-serif',
    color: '#fff',
    textDecoration: 'none',
  },
  nav: {},
  navList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    display: 'flex',
  },
  navItem: {
    marginLeft: '30px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '1.4rem',
    fontFamily: 'Arial, sans-serif',
    transition: 'color 0.3s ease',
  },
  rightSection: {
    display: 'flex',
    alignItems: 'center',
  },
  searchInput: {
    padding: '10px',
    borderRadius: '20px',
    border: 'none',
    background: '#fff',
    color: '#fff',
    marginRight: '20px',
    width: '250px',
    fontSize: '1.2rem',
  },
  cartLink: {
    textDecoration: 'none',
    color: '#fff',
    fontSize: '1.4rem',
    fontFamily: 'Arial, sans-serif',
    display: 'flex',
    alignItems: 'center',
    transition: 'color 0.3s ease',
  }
};

export default Header;
