import React from 'react';

const Header = () => {
  return (
    <header style={styles.header}>
      <div style={styles.container}>
        <h1 style={styles.logo}>Hasaki</h1>
        <nav style={styles.nav}>
          <ul style={styles.navList}>
            <li style={styles.navItem}><a href="/" style={styles.navLink}>Trang Chủ</a></li>
            <li style={styles.navItem}><a href="/products" style={styles.navLink}>Sản Phẩm</a></li>
            <li style={styles.navItem}><a href="/cart" style={styles.navLink}>Giỏ Hàng</a></li>
            {/* Thêm các liên kết và mục điều hướng khác nếu cần */}
          </ul>
        </nav>
        <div style={styles.rightSection}>
          <div style={styles.searchContainer}>
            <input type="text" placeholder="Tìm kiếm..." style={styles.searchInput} />
          </div>
          <a href="/cart" style={styles.cartLink}>
            <i className="fa fa-shopping-cart" style={styles.cartIcon}></i>Giỏ hàng
          </a>
        </div>
      </div>
    </header>
  );
}

const styles = {
  header: {
    backgroundColor: '#ff66b2', // Pink color
    color: '#fff',
    padding: '15px 0',
    borderBottom: '1px solid #ddd',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  container: {
    maxWidth: '1200px',
    margin: '0 auto',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  logo: {
    margin: 0,
    fontSize: '2.5rem', // Increase font size
    fontFamily: 'Arial, sans-serif',
    color: '#fff',
    textDecoration: 'none',
  },
  nav: {},
  navList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    display: 'flex',
  },
  navItem: {
    marginLeft: '30px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '1.4rem', // Increase font size
    fontFamily: 'Arial, sans-serif',
    transition: 'color 0.3s ease',
  },
  rightSection: {
    display: 'flex',
    alignItems: 'center',
  },
  searchContainer: {
    position: 'relative',
    marginRight: '20px',
  },
  searchInput: {
    padding: '12px', // Increase padding
    borderRadius: '25px', // Increase border radius
    border: 'none',
    background: 'rgba(255, 255, 255, 0.8)', // Light pink background
    color: '#333',
    width: '250px', // Increase input width
    fontSize: '1.2rem', // Increase font size
    transition: 'width 0.3s ease',
  },
  cartLink: {
    textDecoration: 'none',
    color: '#fff',
    fontSize: '1.4rem', // Increase font size
    fontFamily: 'Arial, sans-serif',
    display: 'flex',
    alignItems: 'center',
    transition: 'color 0.3s ease',
  },
  cartIcon: {
    marginRight: '5px', // Add some spacing between icon and text
    fontSize: '1.6rem', // Increase icon size
  }
};

export default Header;
