import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const Cart = () => {
  const location = useLocation();
  const [cartItems, setCartItems] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const product = location.state?.product; // Optional chaining

  const handleIncreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.min(prevQuantity + 1, 10)); // Limit quantity to 10
  };

  const handleDecreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1)); // Prevent negative quantity
  };

  const handleAddToCart = () => {
    setCartItems([...cartItems, { product, quantity }]);
    setQuantity(1);
  };

  const handleRemoveFromCart = (index) => {
    const newCartItems = [...cartItems];
    newCartItems.splice(index, 1);
    setCartItems(newCartItems);
  };

  // Calculate total price with proper formatting (assuming currency)
  const totalPrice = cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0)
    .toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <div style={styles.container}>
      <h2>Giỏ hàng của bạn</h2>
      {cartItems.length === 0 ? (
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      ) : (
        <div>
          {cartItems.map((item, index) => (
            <div key={index} style={styles.cartItem}>
              <div style={styles.productInfo}>
                <div style={styles.productImage}>
                  <img src={item.product.image} alt={item.product.name} style={{ maxWidth: '100px' }} />
                </div>
                <div style={styles.productDetails}>
                  <div>{item.product.name}</div>
                  <div>{item.product.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</div>
                  <div>Số lượng: {item.quantity}</div>
                </div>
              </div>
              <button style={styles.removeButton} onClick={() => handleRemoveFromCart(index)}>Xóa</button>
            </div>
          ))}
          <div style={styles.totalPriceContainer}>
            <h3>Tổng tiền: {totalPrice}</h3>
            <button style={styles.checkoutButton}>Thanh toán</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;

const styles = {
  container: {
    padding: '20px',
    backgroundColor: '#f9f9f9',
    maxWidth: '800px',
    margin: '0 auto',
  },
  cartItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: '20px',
  },
  productInfo: {
    display: 'flex',
    alignItems: 'center',
    flex: '1',
  },
  productImage: {
    marginRight: '20px',
  },
  productDetails: {
    flex: '1',
  },
  removeButton: {
    backgroundColor: 'red',
    color: 'white',
    padding: '8px 16px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  totalPriceContainer: {
    marginTop: '20px',
    textAlign: 'right',
  },
  checkoutButton: {
    backgroundColor: 'green',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};
