import React from 'react';

const ProductList = () => {
  // Danh sách sản phẩm từ cửa hàng của bạn (có thể là một array hoặc data từ API)
  const products = [
    { id: 1, name: 'Sản phẩm 1', price: '100.000 VNĐ', description: 'Mô tả sản phẩm 1', image: `${process.env.PUBLIC_URL}/images/sanpham.jpeg` },
    { id: 2, name: 'Sản phẩm 2', price: '150.000 VNĐ', description: 'Mô tả sản phẩm 2', image: `${process.env.PUBLIC_URL}/images/sanpham.jpeg` },
    { id: 3, name: 'Sản phẩm 3', price: '200.000 VNĐ', description: 'Mô tả sản phẩm 3', image: `${process.env.PUBLIC_URL}/images/sanpham.jpeg` },
    // Thêm các sản phẩm khác vào đây
  ];

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Sản Phẩm</h2>
      <div style={styles.productGrid}>
        {products.map(product => (
          <div key={product.id} style={styles.productCard}>
            <div style={styles.productImageContainer}>
              <img src={product.image} alt={product.name} style={styles.productImage} />
              <button style={styles.quickViewButton}>Quick View</button>
            </div>
            <div style={styles.productInfo}>
              <h3 style={styles.productTitle}>{product.name}</h3>
              <p style={styles.productPrice}>Giá: {product.price}</p>
              {/* Thay đổi phần mô tả sản phẩm thành nút "Xem chi tiết sản phẩm" */}
              <button style={styles.detailButton}>Xem chi tiết sản phẩm</button>
              <div style={styles.buttonContainer}>
                <button style={styles.productButton}>Thêm vào giỏ hàng</button>
                <button style={styles.buyNowButton}>Mua ngay</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: '80%',
    margin: '0 auto',
    marginTop: '20px',
  },
  title: {
    fontSize: '2rem',
    color: '#333',
    marginBottom: '20px',
    textAlign: 'center',
  },
  productGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
  },
  productCard: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '20px',
    transition: 'transform 0.3s ease',
    position: 'relative',
    overflow: 'hidden',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  productImageContainer: {
    position: 'relative',
    overflow: 'hidden',
    borderRadius: '10px',
  },
  productImage: {
    width: '100%',
    height: 'auto',
    borderRadius: '10px',
    transition: 'transform 0.3s ease',
  },
  quickViewButton: {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    color: '#fff',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '5px',
    fontSize: '0.8rem',
    cursor: 'pointer',
    opacity: '0',
    transition: 'opacity 0.3s ease',
  },
  productInfo: {
    padding: '10px',
  },
  productTitle: {
    fontSize: '1.2rem',
    color: '#333',
    marginBottom: '10px',
  },
  productPrice: {
    fontSize: '1rem',
    color: '#666',
    marginBottom: '10px',
  },
  // Thêm kiểu cho nút "Xem chi tiết sản phẩm"
  detailButton: {
    backgroundColor: '#4287f5',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '20px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  productDescription: {
    fontSize: '1rem',
    color: '#666',
    marginBottom: '10px',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '10px',
  },
  productButton: {
    backgroundColor: '#326e51',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '20px',
    fontSize: '0.5rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buyNowButton: {
    backgroundColor: '#333',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '20px',
    fontSize: '0.5rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
};

export default ProductList;
