import React from 'react';

const ProductDetail = ({ match }) => {
  // Giả sử thông tin sản phẩm được truyền qua props hoặc được lấy từ API
  const product = {
    id: match.params.id, // Lấy id sản phẩm từ URL
    name: 'Sản phẩm test',
    price: '100.000 VNĐ',
    description: 'Mô tả sản phẩm test',
    image: `${process.env.PUBLIC_URL}/images/sanpham.jpeg`
  };

  return (
    <div style={styles.container}>
      <div style={styles.productDetail}>
        <div style={styles.imageContainer}>
          <img src={product.image} alt={product.name} style={styles.productImage} />
        </div>
        <div style={styles.infoContainer}>
          <h2 style={styles.productName}>{product.name}</h2>
          <p style={styles.productPrice}>Giá: {product.price}</p>
          <p style={styles.productDescription}>{product.description}</p>
          {/* Các thông tin khác về sản phẩm */}
        </div>
      </div>
    </div>
  );
};

const styles = {
  container: {
    maxWidth: '80%',
    margin: '0 auto',
    marginTop: '20px',
  },
  productDetail: {
    display: 'flex',
    alignItems: 'center',
  },
  imageContainer: {
    flex: '0 0 40%',
    marginRight: '20px',
  },
  productImage: {
    width: '100%',
    height: 'auto',
    borderRadius: '10px',
  },
  infoContainer: {
    flex: '1',
  },
  productName: {
    fontSize: '1.5rem',
    color: '#333',
    marginBottom: '10px',
  },
  productPrice: {
    fontSize: '1.2rem',
    color: '#666',
    marginBottom: '10px',
  },
  productDescription: {
    fontSize: '1rem',
    color: '#444',
    marginBottom: '20px',
  },
  // Các styles khác cho thông tin sản phẩm
};

export default ProductDetail;
