import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const Cart = () => {
  const location = useLocation();
  const [quantity, setQuantity] = useState(1);
  const product = location.state?.product; // Optional chaining

  const handleIncreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.min(prevQuantity + 1, 10)); // Limit quantity to 10
  };

  const handleDecreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1)); // Prevent negative quantity
  };

  const handleRemoveFromCart = () => {
    // Implement logic to remove product from cart (replace with your cart actions)
    console.log('Product removed from cart'); // Placeholder for now
  };

  if (!product) {
    return (
      <div>
        <h2>Giỏ hàng của bạn</h2>
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      </div>
    );
  }

  // Calculate total price with proper formatting (assuming currency)
  const totalPrice = (product.price * quantity).toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <div>
      <h2>Giỏ hàng của bạn</h2>
      <div>
        <div>
          <img src={product.image} alt={product.name} style={{ maxWidth: '100px' }} />
        </div>
        <div>
          <div>
            <div>Tên sản phẩm:</div>
            <div>
              <span>{product.name}</span>
              <span>{product.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</span>
              <div>
                <label htmlFor="quantity">Số lượng: </label>
                <div>
                  <button onClick={handleDecreaseQuantity}>-</button>
                  <input
                    type="number"
                    id="quantity"
                    name="quantity"
                    value={quantity}
                    onChange={(e) => setQuantity(Math.max(parseInt(e.target.value, 10), 1))}
                    min={1}
                    max={10}
                    style={{ padding: '5px', borderRadius: '5px', border: '1px solid #ccc' }}
                  />
                  <button onClick={handleIncreaseQuantity}>+</button>
                </div>
              </div>
              <button onClick={handleRemoveFromCart}>Xóa khỏi giỏ hàng</button>
            </div>
          </div>
        </div>
      </div>
      <div>
        <h3>Tổng tiền: {totalPrice}</h3>
        <button>Thanh toán</button>
      </div>
    </div>
  );
};

export default Cart;
