import React, { useState } from 'react';

const HomePage = () => {
  return (
    <div style={styles.container}>
      <Header />
      <HeroSection />
      <FeaturedProducts />
      <AboutUs />
    </div>
  );
}

const Header = () => {
  return (
    <header style={{ ...styles.header, ...styles.shadow, ...styles.headerLarge }}>
      <div style={styles.container}>
        <h1 style={{ ...styles.title, ...styles.greenText }}>Cửa Hàng Mỹ Phẩm Hasaki</h1>
        <p style={{ ...styles.subtitle, ...styles.greenText }}>Chào mừng đến với Hasaki! Khám phá bộ sưu tập sản phẩm chăm sóc sắc đẹp cao cấp của chúng tôi.</p>
      </div>
      <Slider />
    </header>
  );
}

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const images = [
    `${process.env.PUBLIC_URL}/images/slide1.jpeg`,
    `${process.env.PUBLIC_URL}/images/slide2.jpeg`,
    `${process.env.PUBLIC_URL}/images/slide3.jpeg`
  ];

  return (
    <div style={styles.slider}>
      <img src={images[currentSlide]} alt={`Slide ${currentSlide + 1}`} style={styles.slide} />
    </div>
  );
}

const HeroSection = () => {
  return (
    <section style={{ ...styles.heroSection, ...styles.shadow }}>
      <div style={styles.heroContent}>
        <h2 style={{ ...styles.heroTitle, ...styles.greenText }}>Khám Phá Sắc Đẹp Tự Nhiên</h2>
        <p style={styles.heroDescription}>Chúng tôi cam kết mang lại những sản phẩm mỹ phẩm tự nhiên, an toàn và hiệu quả nhất cho bạn.</p>
        <button style={{ ...styles.heroButton, ...styles.buttonHover }}>Tìm Hiểu Thêm</button>
      </div>
    </section>
  );
}

const ProductCard = ({ handleAddToCart, handleBuyNow }) => {
  const product = {
    name: 'Tên Sản Phẩm',
    price: '100.000 VNĐ',
    image: `${process.env.PUBLIC_URL}/images/sanpham.jpeg`
  };

  return (
    <div style={styles.productCard}>
      <div style={styles.productImageContainer}>
        <img src={product.image} alt="Sản phẩm" style={styles.productImage} />
        <button style={styles.quickViewButton}>Quick View</button>
      </div>
      <div style={styles.productInfo}>
        <h3 style={styles.productTitle}>{product.name}</h3>
        <p style={styles.productPrice}>Giá: {product.price}</p>
        <button onClick={() => handleAddToCart(product)} style={styles.productButton}>Thêm vào giỏ hàng</button>
        <button onClick={() => handleBuyNow(product)} style={styles.productButton}>Mua ngay</button>
      </div>
    </div>
  );
}

const FeaturedProducts = () => {
  const handleAddToCart = (product) => {
    console.log('Thêm sản phẩm vào giỏ hàng:', product);
    // Thêm logic để xử lý việc thêm sản phẩm vào giỏ hàng
  };

  const handleBuyNow = (product) => {
    console.log('Mua ngay sản phẩm:', product);
    // Thêm logic để xử lý việc mua ngay sản phẩm
  };

  return (
    <section style={styles.featuredProducts}>
      <h2 style={styles.sectionTitle}>Sản Phẩm Nổi Bật</h2>
      <div style={styles.productGrid}>
        {[...Array(4).keys()].map((_, index) => (
          <ProductCard key={index} handleAddToCart={handleAddToCart} handleBuyNow={handleBuyNow} />
        ))}
      </div>
    </section>
  );
}

const AboutUs = () => {
  return (
    <section style={styles.aboutUs}>
      <h2 style={{ ...styles.sectionTitle, ...styles.greenText }}>Về Chúng Tôi</h2>
      <p style={styles.aboutDescription}>Chúng tôi luôn nỗ lực mang đến những sản phẩm mỹ phẩm tốt nhất cho khách hàng của chúng tôi. Sản phẩm của chúng tôi được chọn lựa cẩn thận để tăng cường vẻ đẹp tự nhiên của bạn.</p>
    </section>
  );
}

const styles = {
  container: {
    maxWidth: '80%',
    margin: '0 auto',
  },
  header: {
    textAlign: 'center',
    marginBottom: '20px',
    padding: '20px',
    position: 'relative',
  },
  headerLarge: {
    width: '96.5%',
    padding: '40px 20px',
  },

  title: {
    fontSize: '2.5rem',
    color: '#333',
  },
  subtitle: {
    fontSize: '1.2rem',
    color: '#666',
  },
  slider: {
    perspective: '1500px',
    position: 'relative',
    overflow: 'hidden',
