    marginBottom: '20px',
  },
  heroButton: {
    backgroundColor: '#326e51',
    color: '#fff',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '20px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  sectionTitle: {
    fontSize: '2rem',
    color: '#333',
    marginBottom: '20px',
  },


  featuredProducts: {
