    fontSize: '2rem',
    color: '#326e51',
    marginBottom: '20px',
  },
  productGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '20px',
  },
  productCard: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '20px',
    transition: 'transform 0.3s ease',
    position: 'relative',
    overflow: 'hidden',
  },
  productImageContainer: {
