import React, { useState } from 'react';

const AdminPage = () => {
  const [products, setProducts] = useState([
    {
      name: 'Product 1',
      price: 10,
      description: 'Description of Product 1',
      image: 'https://example.com/product1.jpg'
    },
    {
      name: 'Product 2',
      price: 20,
      description: 'Description of Product 2',
      image: 'https://example.com/product2.jpg'
    },
    // Add more example products here if needed
  ]);
  const [newProductVisible, setNewProductVisible] = useState(false);
  const [newProduct, setNewProduct] = useState({ name: '', price: 0, description: '', image: '' });
  const [editProductIndex, setEditProductIndex] = useState(null);
  const [editedProduct, setEditedProduct] = useState({ name: '', price: 0, description: '', image: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editProductIndex !== null) {
      setEditedProduct({ ...editedProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleAddProduct = () => {
    setProducts([...products, newProduct]);
    setNewProduct({ name: '', price: 0, description: '', image: '' });
    setNewProductVisible(false); // Hide the form after adding a product
  };

  const handleDeleteProduct = (index) => {
    const updatedProducts = [...products];
    updatedProducts.splice(index, 1);
    setProducts(updatedProducts);
  };

  const handleEditProduct = (index) => {
    setEditProductIndex(index);
    setEditedProduct(products[index]);
  };

  const handleSaveEdit = () => {
    const updatedProducts = [...products];
    updatedProducts[editProductIndex] = editedProduct;
    setProducts(updatedProducts);
    setEditProductIndex(null);
    setEditedProduct({ name: '', price: 0, description: '', image: '' });
  };

  return (
    <div className="admin-container">
      <div className="header">
        <h2 className="title">Quản lý sản phẩm</h2>
        <button className="add-product-button" onClick={() => setNewProductVisible(true)}>Thêm sản phẩm mới</button>
      </div>
      <table className="product-table">
        <thead>
          <tr>
            <th>Tên sản phẩm</th>
            <th>Giá</th>
            <th>Mô tả</th>
            <th>Hình ảnh</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={index}>
              <td>{product.name}</td>
              <td>{product.price}</td>
              <td>{product.description}</td>
              <td>
                <img src={product.image} alt={product.name} className="product-image" />
              </td>
              <td>
                <button className="edit-button" onClick={() => handleEditProduct(index)}>Edit</button>
                <button className="delete-button" onClick={() => handleDeleteProduct(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {newProductVisible && (
        <div className="add-product-form">
          <h2>Thêm sản phẩm mới</h2>
          <input
            type="text"
            placeholder="Tên sản phẩm"
            name="name"
            value={newProduct.name}
            onChange={handleInputChange}
          />
          <input
            type="number"
            placeholder="Giá"
            name="price"
            value={newProduct.price}
            onChange={handleInputChange}
          />
          <input
            type="text"
            placeholder="Mô tả"
            name="description"
            value={newProduct.description}
            onChange={handleInputChange}
          />
          <input
            type="text"
            placeholder="URL hình ảnh"
            name="image"
            value={newProduct.image}
            onChange={handleInputChange}
          />
          <button className="submit-button" onClick={handleAddProduct}>Thêm sản phẩm</button>
          <button className="cancel-button" onClick={() => setNewProductVisible(false)}>Hủy</button>
        </div>
      )}
      {editProductIndex !== null && (
        <div className="edit-product-form">
          <h2>Chỉnh sửa sản phẩm</h2>
          <input
            type="text"
            placeholder="Tên sản phẩm"
            name="name"
            value={editedProduct.name}
            onChange={handleInputChange}
          />
          <input
            type="number"
            placeholder="Giá"
            name="price"
            value={editedProduct.price}
            onChange={handleInputChange}
          />
          <input
            type="text"
            placeholder="Mô tả"
            name="description"
            value={editedProduct.description}
            onChange={handleInputChange}
          />
          <input
            type="text"
            placeholder="URL hình ảnh"
            name="image"
            value={editedProduct.image}
            onChange={handleInputChange}
          />
          <button className="save-edit-button" onClick={handleSaveEdit}>Lưu chỉnh sửa</button>
        </div>
      )}
    </div>
  );
};

export default AdminPage;

// CSS
const styles = `
.admin-container {
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  font-family: Arial, sans-serif;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

.title {
  margin: 0;
}

.product-table {
  width: 100%;
  border-collapse: collapse;
}

.product-table th, .product-table td {
  border: 1px solid #ddd;
  padding: 8px;
  text-align: left;
}

.product-table th {
  background-color: #f2f2f2;
}

.product-image {
  width: 100px;
  height: auto;
}

.add-product-button, .submit-button, .cancel-button, .edit-button, .delete-button, .save-edit-button {
  padding: 10px 20px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
}

.add-product-button {
  background-color: #28a745;
  color: #fff;
}

.submit-button, .edit-button {
  background-color: #007bff;
  color: #fff;
}

.cancel-button, .delete-button {
  background-color: #dc3545;
  color: #fff;
}

.save-edit-button {
  background-color: #17a2b8;
  color: #fff;
}

.add-product-form, .edit-product-form {
  margin-top: 20px;
}

.add-product-form input, .edit-product-form input {
  margin-bottom: 10px;
  padding: 8px;
  width: calc(100% - 16px);
}
`;

// Add CSS to the head of the document
const styleElement = document.createElement('style');
styleElement.innerHTML = styles;
document.head.appendChild(styleElement);
