import React from 'react';

const ProductDetail = ({ product, onClose }) => {
  return (
    <div style={styles.overlay}>
      <div style={styles.modal}>
        <button style={styles.closeButton} onClick={onClose}>Đóng</button>
        <div style={styles.productDetailContainer}>
          <img src={product.image} alt={product.name} style={styles.productImage} />
          <div style={styles.productInfo}>
            <h2 style={styles.productName}>{product.name}</h2>
            <p style={styles.productPrice}>Giá: {product.price}</p>
            <p style={styles.productDescription}>{product.description}</p>
            <button style={styles.addToCartButton}>Thêm vào giỏ hàng</button>
            <button style={styles.buyNowButton}>Mua ngay</button>
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1000,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  modal: {
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '20px',
    maxWidth: '80%',
    overflow: 'auto',
  },
  closeButton: {
    position: 'absolute',
    top: '10px',
    right: '10px',
    backgroundColor: '#ccc',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '5px',
    fontSize: '0.8rem',
    cursor: 'pointer',
  },
  productDetailContainer: {
    display: 'flex',
  },
  productImage: {
    width: '300px',
    height: 'auto',
    marginRight: '20px',
    borderRadius: '10px',
  },
  productInfo: {
    flex: 1,
  },
  productName: {
    fontSize: '1.5rem',
    color: '#333',
    marginBottom: '10px',
  },
  productPrice: {
    fontSize: '1.2rem',
    color: '#666',
    marginBottom: '10px',
  },
  productDescription: {
    fontSize: '1rem',
    color: '#333',
    marginBottom: '20px',
  },
  addToCartButton: {
    backgroundColor: '#CD950C',
    color: '#000',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '20px',
    fontSize: '1rem',
    cursor: 'pointer',
    marginRight: '10px',
  },
  buyNowButton: {
    backgroundColor: '#CD3333',
    color: '#000',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '20px',
    fontSize: '1rem',
    cursor: 'pointer',
  },
};

export default ProductDetail;
