import React, { useState } from 'react';

const HomePage = () => {
  return (
    <div style={styles.container}>
      <Header />
      <HeroSection />
      <FeaturedProducts />
      <AboutUs />
    </div>
  );
}

const Header = () => {
  return (
    <header style={{ ...styles.header, ...styles.shadow }}>
      <div style={styles.container}>
        <h1 style={{...styles.title, ...styles.greenText}}>Cửa Hàng Mỹ Phẩm Hasaki</h1>
        <p style={{...styles.subtitle, ...styles.greenText}}>Chào mừng bạn đến với Cửa Hàng Mỹ Phẩm Hasaki! Khám phá bộ sưu tập sản phẩm chăm sóc sắc đẹp cao cấp của chúng tôi.</p>
      </div>
      <Slider />
    </header>
  );
}

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((currentSlide + 1) % images.length);
  }

  const prevSlide = () => {
    setCurrentSlide((currentSlide + images.length - 1) % images.length);
  }

  const images = ['slide1.jpg', 'slide2.jpg', 'slide3.jpg'];

  return (
    <div style={styles.slider}>
      <img src={images[currentSlide]} alt={`Slide ${currentSlide + 1}`} style={styles.slide} />
      <button style={styles.prevButton} onClick={prevSlide}>&#10094;</button>
      <button style={styles.nextButton} onClick={nextSlide}>&#10095;</button>
    </div>
  );
}

const HeroSection = () => {
  return (
    <section style={{ ...styles.heroSection, ...styles.shadow }}>
      <div style={styles.heroContent}>
        <h2 style={{...styles.heroTitle, ...styles.greenText}}>Khám Phá Sắc Đẹp Tự Nhiên</h2>
        <p style={styles.heroDescription}>Chúng tôi cam kết mang lại những sản phẩm mỹ phẩm tự nhiên, an toàn và hiệu quả nhất cho bạn.</p>
        <button style={{...styles.heroButton, ...styles.buttonHover}}>Tìm Hiểu Thêm</button>
      </div>
    </section>
  );
}

const FeaturedProducts = () => {
  return (
    <section style={styles.featuredProducts}>
      <h2 style={{...styles.sectionTitle, ...styles.greenText}}>Sản Phẩm Nổi Bật</h2>
      <div style={styles.productContainer}>
        <ProductCard />
        <ProductCard />
        <ProductCard />
        <ProductCard />
      </div>
    </section>
  );
}

const ProductCard = () => {
  return (
    <div style={{ ...styles.productCard, ...styles.shadow, ...styles.productCardHover }}>
      <img src="product.jpg" alt="Product" style={styles.productImage} />
      <h3 style={{...styles.productTitle, ...styles.greenText}}>Tên Sản Phẩm</h3>
      <p style={styles.productPrice}>Giá: 100.000 VNĐ</p>
      <button style={{...styles.productButton, ...styles.buttonHover}}>Thêm vào giỏ hàng</button>
    </div>
  );
}

const AboutUs = () => {
  return (
    <section style={styles.aboutUs}>
      <h2 style={{...styles.sectionTitle, ...styles.greenText}}>Về Chúng Tôi</h2>
      <p style={styles.aboutDescription}>Chúng tôi luôn nỗ lực mang đến những sản phẩm mỹ phẩm tốt nhất cho khách hàng của chúng tôi. Sản phẩm của chúng tôi được chọn lựa cẩn thận để tăng cường vẻ đẹp tự nhiên của bạn.</p>
    </section>
  );
}

const styles = {
  container: {
    maxWidth: '800px',
    margin: '0 auto',
    padding: '20px',
  },
  header: {
    textAlign: 'center',
    marginBottom: '40px',
    padding: '20px',
    position: 'relative',
  },
  title: {
    fontSize: '2.5rem',
    color: '#333',
  },
  subtitle: {
    fontSize: '1.2rem',
    color: '#666',
  },
  slider: {
    perspective: '1500px',
    position: 'relative',
    overflow: 'hidden',
    margin: '20px 0',
    borderRadius: '10px',
  },
  slide: {
    width: '100%',
    height: '300px',
    objectFit: 'cover',
    borderRadius: '10px',
    transition: 'transform 1s',
    backfaceVisibility: 'hidden',
  },
  prevButton: {
    position: 'absolute',
    top: '50%',
    left: '10px',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    color: '#fff',
    border: 'none',
    fontSize: '24px',
    padding: '10px',
    borderRadius: '50%',
    cursor: 'pointer',
    zIndex: '1',
  },
  nextButton: {
    position: 'absolute',
    top: '50%',
    right: '10px',
    transform: 'translateY(-50%)',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    color: '#fff',
    border: 'none',
    fontSize: '24px',
    padding: '10px',
    borderRadius: '50%',
    cursor: 'pointer',
    zIndex: '1',
  },
  heroSection: {
    background: 'linear-gradient(135deg, #fdfcfb, #e2d1c3)',
    padding: '60px 0',
    textAlign: 'center',
    marginBottom: '40px',
    borderRadius: '10px',
  },
  heroContent: {
    maxWidth: '600px',
    margin: '0 auto',
  },
  heroTitle: {
    fontSize: '2rem',
    color: '#333',
    marginBottom: '20px',
  },
  heroDescription: {
    fontSize: '1.2rem',
    color: '#666',
    marginBottom: '20px',
  },
  heroButton: {
    backgroundColor: '#326e51',
    color: '#fff',
    border: 'none',
    padding: '12px 24px',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  sectionTitle: {
    fontSize: '2rem',
    color: '#333',
    marginBottom: '20px',
  },
  productContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
  },
  productCard: {
    width: '30%',
    marginBottom: '20px',
    backgroundColor: '#fff',
    borderRadius: '10px',
    padding: '20px',
    transition: 'transform 0.3s ease',
    position: 'relative',
    overflow: 'hidden',
  },
  productCardHover: {
    ':hover': {
      transform: 'scale(1.05)',
    },
  },
  productImage: {
    width: '100%',
    borderRadius: '5px',
    marginBottom: '10px',
  },
  productTitle: {
    fontSize: '1.2rem',
    color: '#333',
    marginBottom: '10px',
  },
  productPrice: {
    fontSize: '1rem',
    color: '#666',
    marginBottom: '10px',
  },
  productButton: {
    backgroundColor: '#326e51',
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '5px',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  aboutUs: {
    marginBottom: '40px',
  },
  aboutDescription: {
    fontSize: '1.2rem',
    color: '#666',
  },
  shadow: {
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  border: {
    borderRadius: '5px',
    border: '1px solid #ddd',
  },
  greenText: {
    color: '#326e51',
  },

  buttonHover: {
    ':hover': {
      backgroundColor: '#4CAF50',
    },
  },
};

export default HomePage;
