import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Cart.css'; // Import CSS file for styling

const Cart = () => {
  const location = useLocation();
  const [quantity, setQuantity] = useState(1);
  const product = location.state?.product;

  const handleIncreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.min(prevQuantity + 1, 10));
  };

  const handleDecreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1));
  };

  const handleRemoveFromCart = () => {
    console.log('Product removed from cart');
  };

  if (!product) {
    return (
      <div className="cart-empty">
        <h2>Giỏ hàng của bạn</h2>
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      </div>
    );
  }

  const totalPrice = (product.price * quantity).toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      <div className="cart-item">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-info">
          <div className="info">
            <div className="product-name">Tên sản phẩm:</div>
            <div className="product-name">{product.name}</div>
          </div>
          <div className="info">
            <div className="product-price">Giá:</div>
            <div className="product-price">{product.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</div>
          </div>
          <div className="quantity-control">
            <label htmlFor="quantity">Số lượng: </label>
            <div className="quantity-input">
              <button className="quantity-button" onClick={handleDecreaseQuantity}>-</button>
              <input
                type="number"
                id="quantity"
                name="quantity"
                value={quantity}
                onChange={(e) => setQuantity(Math.max(parseInt(e.target.value, 10), 1))}
                min={1}
                max={10}
              />
              <button className="quantity-button" onClick={handleIncreaseQuantity}>+</button>
            </div>
          </div>
          <button onClick={handleRemoveFromCart} className="remove-button">Xóa khỏi giỏ hàng</button>
        </div>
      </div>
      <div className="cart-summary">
        <h3>Tổng tiền: {totalPrice}</h3>
        <button className="checkout-button">Thanh toán</button>
      </div>
    </div>
  );
};

export default Cart;

