import React, { useState } from 'react';

const Login = ({ onClose }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Thực hiện xử lý đăng nhập, ví dụ: gửi dữ liệu đăng nhập đến server
    console.log('Username:', username);
    console.log('Password:', password);
    // Sau khi xử lý đăng nhập, bạn có thể đóng modal hoặc thực hiện hành động khác
    onClose();
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#326e51' }}>
      <div style={{ backgroundColor: '#ffffff', padding: '40px', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
        <h2 style={{ marginBottom: '20px', color: '#326e51' }}>Đăng Nhập</h2>
        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label htmlFor="username" style={{ display: 'block', fontWeight: 'bold', marginBottom: '5px', color: '#333333' }}>Tên đăng nhập:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              style={{ width: '100%', padding: '10px', border: '1px solid #cccccc', borderRadius: '4px', outline: 'none' }}
            />
          </div>
          <div style={{ marginBottom: '20px' }}>
            <label htmlFor="password" style={{ display: 'block', fontWeight: 'bold', marginBottom: '5px', color: '#333333' }}>Mật khẩu:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ width: '100%', padding: '10px', border: '1px solid #cccccc', borderRadius: '4px', outline: 'none' }}
            />
          </div>
          <button type="submit" style={{ width: '100%', padding: '10px', backgroundColor: '#326e51', color: '#ffffff', border: 'none', borderRadius: '4px', cursor: 'pointer', outline: 'none', transition: 'background-color 0.3s' }}>Đăng Nhập</button>
        </form>
      </div>
    </div>
  );
};

export default Login;
