import React from 'react';

const Header = () => {
  return (
    <header style={styles.header}>
      <h1 style={styles.title}>Hasaki</h1>
      <nav style={styles.nav}>
        <ul style={styles.navList}>
          <li style={styles.navItem}><a href="/" style={styles.navLink}>Trang Chủ</a></li>
          <li style={styles.navItem}><a href="/products" style={styles.navLink}>Sản Phẩm</a></li>
          <li style={styles.navItem}><a href="/cart" style={styles.navLink}>Giỏ Hàng</a></li>
          {/* Thêm các liên kết và mục điều hướng khác nếu cần */}
        </ul>
      </nav>
    </header>
  );
}

const styles = {
  header: {
    backgroundColor: '#ff66b2',
    color: '#fff',
    padding: '20px',
    textAlign: 'center',
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  },
  title: {
    margin: 0,
    fontSize: '2rem',
    fontFamily: 'Arial, sans-serif',
  },
  nav: {
    marginTop: '20px',
  },
  navList: {
    listStyleType: 'none',
    padding: 0,
    margin: 0,
    display: 'flex',
    justifyContent: 'center',
  },
  navItem: {
    marginRight: '20px',
  },
  navLink: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '1.2rem',
    fontFamily: 'Arial, sans-serif',
    transition: 'color 0.3s ease',
  },
};

export default Header;
