import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const navigate = useNavigate(); // Sử dụng useNavigate thay vì useHistory

  const location = useLocation();
  const [quantity, setQuantity] = useState(1);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const product = location.state?.product;


  const handleBuyNow = (product) => {
    // Logic khi click vào nút Mua Ngay
    setShowNotification(true);
    setTimeout(() => {
      setShowNotification(false);
      navigate('/cart', { state: { product } }); // Sử dụng navigate
    }, 2000);
  };

  const handleIncreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.min(prevQuantity + 1, 10)); // Limit quantity to 10
  };

  const handleDecreaseQuantity = () => {
    setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1)); // Prevent negative quantity
  };

  const handleRemoveFromCart = () => {
    setShowConfirmation(true);
  };

  const confirmRemoveFromCart = () => {
    console.log('Product removed from cart');
    setShowConfirmation(false);
  };

  const cancelRemoveFromCart = () => {
    setShowConfirmation(false);
  };

  if (!product) {
    return (
      <div style={styles.container}>
        <h2>Giỏ hàng của bạn</h2>
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      </div>
    );
  }

  const totalPrice = (product.price * quantity).toLocaleString('vi-VN', { style: 'currency', currency: 'VND' });

  return (
    <div style={styles.container}>
      <h2>Giỏ hàng của bạn</h2>
      <div style={styles.productContainer}>
        <div style={styles.productImage}>
          <img src={product.image} alt={product.name} style={styles.image} />
        </div>
        <div style={styles.productDetails}>
          <div style={styles.productName}>{product.name}</div>
          <div style={styles.productPrice}>{product.price.toLocaleString('vi-VN', { style: 'currency', currency: 'VND' })}</div>
          <div style={styles.productQuantity}>
            <label htmlFor="quantity">Số lượng: </label>
            <div style={styles.quantityControls}>
              <button onClick={handleDecreaseQuantity} style={styles.quantityButton}>-</button>
              <input
                type="number"
                id="quantity"
                name="quantity"
                value={quantity}
                onChange={(e) => setQuantity(Math.max(parseInt(e.target.value, 10), 1))}
                min={1}
                max={10}
                style={styles.quantityInput}
              />
              <button onClick={handleIncreaseQuantity} style={styles.quantityButton}>+</button>
            </div>
          </div>
          <button style={styles.removeButton} onClick={handleRemoveFromCart}>Xóa khỏi giỏ hàng</button>
        </div>
      </div>
      <div style={styles.totalPriceContainer}>
        <h3 style={styles.totalPrice}>Tổng tiền: {totalPrice}</h3>
        <button style={styles.checkoutButton} onClick={handleBuyNow}>Thanh toán</button>
      </div>
      {showConfirmation && (
        <div style={styles.modal}>
          <div style={styles.modalContent}>
            <p>Bạn có chắc chắn muốn xóa sản phẩm này khỏi giỏ hàng không?</p>
            <div>
              <button style={styles.confirmButton} onClick={confirmRemoveFromCart}>Xóa</button>
              <button style={styles.cancelButton} onClick={cancelRemoveFromCart}>Hủy</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;

const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '35px',
    border: '1px solid #ccc',
    borderRadius: '10px',
    backgroundColor: '#f9f9f9',
  },
  productContainer: {
    display: 'flex',
    marginBottom: '20px',
  },
  productImage: {
    marginRight: '20px',
  },
  image: {
    maxWidth: '200px',
    borderRadius: '20px',
  },
  productDetails: {
    flex: '1',
  },
  productName: {
    fontWeight: 'bold', // Thêm thuộc tính fontWeight để làm chữ in đậm

    fontSize: '20px',
    marginBottom: '10px',
  },
  productPrice: {
    fontSize: '16px',
    marginBottom: '40px',
  },
  productQuantity: {
    marginBottom: '30px',
  },
  quantityControls: {
    display: 'flex',
    alignItems: 'center',
  },
  quantityButton: {
    backgroundColor: '#ccc',
    color: '#333',
    border: 'none',
    borderRadius: '50%',
    width: '30px',
    height: '30px',
    cursor: 'pointer',
  },
  quantityInput: {
    padding: '5px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    margin: '0 10px',
    width: '50px',
    textAlign: 'center',
  },
  removeButton: {
    backgroundColor: 'red',
    color: '#fff',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  totalPriceContainer: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginTop: '20px',
  },
  totalPrice: {
    fontSize: '18px',
  },
  checkoutButton: {
    backgroundColor: 'green',
    color: '#fff',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  modal: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    zIndex: 9999,
  },
  modalContent: {
    backgroundColor: '#fff',
    padding: '20px',
    borderRadius: '5px',
    textAlign: 'center',
  },
  confirmButton: {
    backgroundColor: 'red',
    color: 'white',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    marginRight: '10px',
  },
  cancelButton: {
    backgroundColor: '#ccc',
    color: 'black',
    padding: '10px 20px',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};
