import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const Cart = () => {
  const location = useLocation();
  const [quantity, setQuantity] = useState(1);
  const product = location.state?.product; // Sử dụng optional chaining

  const handleIncreaseQuantity = () => {
    setQuantity(quantity + 1);
  };

  const handleDecreaseQuantity = () => {
    if (quantity > 1) {
      setQuantity(quantity - 1);
    }
  };

  const handleRemoveFromCart = () => {
    // Xử lý logic xóa sản phẩm khỏi giỏ hàng
  };

  if (!product) {
    return (
      <div>
        <h2>Giỏ hàng của bạn</h2>
        <p>Không có sản phẩm nào trong giỏ hàng.</p>
      </div>
    );
  }

  return (
    <div>
      <h2>Giỏ hàng của bạn</h2>
      <div>
        <p>Tên sản phẩm: {product.name}</p>
        <p>Giá: {product.price}</p>
        <div>
                  <label htmlFor="quantity">Số lượng: </label>
                  <select id="quantity" name="quantity" style={{ padding: '5px', borderRadius: '15px', border: '1px solid #ccc', marginRight: '15px' }}>
                    {[...Array(10).keys()].map((num) => (
                      <option key={num + 1} value={num + 1}>{num + 1}</option>
                    ))}
                  </select>
                </div>
        <button onClick={handleRemoveFromCart}>Xóa khỏi giỏ hàng</button>
      </div>
      {/* Hiển thị các thông tin khác của sản phẩm */}
      <div>
        <h3>Tổng tiền: {product.price * quantity}</h3>
        <button>Thanh toán</button>
      </div>
    </div>
  );
};

export default Cart;
