import React from 'react';

const Footer = () => {
  return (
    <footer style={styles.footer}>
      <div style={styles.container}>
        <div style={styles.footerContent}>
          <div style={styles.footerColumn}>
            <h4 style={styles.footerHeading}>Liên Kết Hữu Ích</h4>
            <ul style={styles.footerLinks}>
              <li><a href="#">Trang Chủ</a></li>
              <li><a href="#">Sản Phẩm</a></li>
              <li><a href="#">Giới Thiệu</a></li>
              <li><a href="#">Liên Hệ</a></li>
            </ul>
          </div>
          <div style={styles.footerColumn}>
            <h4 style={styles.footerHeading}>Theo Dõi Chúng Tôi</h4>
            <ul style={styles.socialIcons}>
              <li><a href="#"><i className="fab fa-facebook-f"></i> Facebook</a></li>
              <li><a href="#"><i className="fab fa-twitter"></i> Twitter</a></li>
              <li><a href="#"><i className="fab fa-instagram"></i> Instagram</a></li>
            </ul>
          </div>
          <div style={styles.footerColumn}>
            <h4 style={styles.footerHeading}>Liên Hệ</h4>
            <p>123 Đường ABC, Thành Phố XYZ</p>
            <p>Email: info@example.com</p>
            <p>Điện Thoại: 123-456-789</p>
          </div>
        </div>
        <p style={styles.footerText}>© 2024 Cửa Hàng Mỹ Phẩm Hasaki. All Rights Reserved.</p>
      </div>
    </footer>
  );
}

const styles = {
  footer: {
    backgroundColor: '#326e51',
    color: '#fff',
    padding: '40px 0',
    boxShadow: '0px -3px 10px rgba(0, 0, 0, 0.1)', // Hiệu ứng mềm mại 3D
  },
  container: {
    maxWidth: '80%',
    margin: '0 auto',
  },
  footerContent: {
    display: 'flex',
    justifyContent: 'space-between',
    flexWrap: 'wrap',
  },
  footerColumn: {
    flex: '1 1 300px',
    marginBottom: '20px',
  },
  footerHeading: {
    fontSize: '1.2rem',
    marginBottom: '10px',
  },
  footerLinks: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
  },
  socialIcons: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    fontSize: '1.2rem',
  },
  footerText: {
    textAlign: 'center',
    fontSize: '0.9rem',
    marginTop: '20px',
  },
};

export default Footer;
